$('input:radio[id="xiaoxuan_color"]').change(function(){
	$.cookie('color_xiaoxuan', null);
	var color = $('input:radio[id="xiaoxuan_color"]:checked').val();
	$.cookie('color_xiaoxuan',color,{
		expires:7
	});
	location.reload();
});
get_color();
function get_color() {
	if(!$.cookie('color_xiaoxuan')){
		$("body[id='xiaoxuan_thmem_color']").attr("data-ma-theme","blue-grey");
		$("label[class='btn bg-blue-grey']").attr("class","btn bg-btn bg-blue-grey active");
	}else{
		$("body[id='xiaoxuan_thmem_color']").attr("data-ma-theme",$.cookie('color_xiaoxuan'));
		$("label[class='btn bg-"+$.cookie('color_xiaoxuan')+"']").attr("class","btn bg-"+$.cookie('color_xiaoxuan')+" active");
	}
}
$('.dropdown-menu-right').click(function () {
	$('.dropdown-menu-right').hide()
});
